package com.pearson.pixb2b.vendor.transaction.inbound.orderstatus.dto;


/**
 * PODate is a data transfer object to store the 
 * Issued Date details for Purchase Order and transfer the same between classes.
 * 
 * @author Ashish Agrawal
 */
public class PODate implements java.io.Serializable {
	private static final long serialVersionUID = 1573939108348505544L;
	
	private String day		= null;
	private String month	= null;
	private String year		= null;
	/**
	 * Default constructor.
	 */
	public PODate() {
		super();
	}
	/**
	 * @return the day
	 */
	public String getDay() {
		return day;
	}
	/**
	 * @param day the day to set
	 */
	public void setDay(String day) {
		this.day = day;
	}
	/**
	 * @return the month
	 */
	public String getMonth() {
		return month;
	}
	/**
	 * @param month the month to set
	 */
	public void setMonth(String month) {
		this.month = month;
	}
	/**
	 * @return the year
	 */
	public String getYear() {
		return year;
	}
	/**
	 * @param year the year to set
	 */
	public void setYear(String year) {
		this.year = year;
	}
}
